"use client";

import * as React from "react";
import * as AccordionPrimitive from "@radix-ui/react-accordion";
import { Plus } from "lucide-react";
import { cn } from "@/lib/utils";

const Accordion = AccordionPrimitive.Root;

const AccordionItem = React.forwardRef(
  ({ className, ...props }, ref) => (
    <AccordionPrimitive.Item
      ref={ref}
      className={cn("border-b", className)}
      {...props}
    />
  )
);
AccordionItem.displayName = "AccordionItem";

const AccordionTrigger = React.forwardRef(
  ({ className, children, ...props }, ref) => (
    <AccordionPrimitive.Header className="flex">
      <AccordionPrimitive.Trigger
        ref={ref}
        className={cn(
          "flex flex-1 items-center justify-between py-4 text-sm font-medium text-foreground transition-all hover:underline text-left",
          "[&[data-state=open]>svg]:rotate-45",
          "transition-transform duration-300 ease-in-out",
          className
        )}
        {...props}
      >
        {children}
        <Plus className="h-4 w-4 shrink-0 text-primary transition-transform duration-300 ease-in-out" />
      </AccordionPrimitive.Trigger>
    </AccordionPrimitive.Header>
  )
);
AccordionTrigger.displayName = AccordionPrimitive.Trigger.displayName;

const AccordionContent = React.forwardRef(
  ({ className, children, ...props }, ref) => (
    <AccordionPrimitive.Content
      ref={ref}
      className={cn(
        "overflow-hidden text-sm text-foreground transition-all",
        "data-[state=open]:animate-accordion-down data-[state=closed]:animate-accordion-up"
      )}
      {...props}
    >
      <div
        className={cn(
          "w-full p-5 rounded-xl bg-muted/60 backdrop-blur-sm border border-border animate-shake-smooth flex items-center",
          className
        )}
      >
        {children}
      </div>
    </AccordionPrimitive.Content>
  )
);
AccordionContent.displayName = AccordionPrimitive.Content.displayName;

export { Accordion, AccordionItem, AccordionTrigger, AccordionContent };

/** @type {import('tailwindcss').Config} */
module.exports = {
  theme: {
    extend: {
      keyframes: {
        "accordion-down": {
          from: { height: "0" },
          to: { height: "var(--radix-accordion-content-height)" }
        },
        "accordion-up": {
          from: { height: "var(--radix-accordion-content-height)" },
          to: { height: "0" }
        },
        "shake-smooth": {
          "0%": { transform: "translateX(0)" },
          "10%": { transform: "translateX(-12px)" },
          "20%": { transform: "translateX(12px)" },
          "30%": { transform: "translateX(-10px)" },
          "40%": { transform: "translateX(10px)" },
          "50%": { transform: "translateX(-6px)" },
          "60%": { transform: "translateX(6px)" },
          "70%": { transform: "translateX(-3px)" },
          "80%": { transform: "translateX(3px)" },
          "90%": { transform: "translateX(-1px)" },
          "100%": { transform: "translateX(0)" }
        }
      },
      animation: {
        "accordion-down": "accordion-down 0.2s ease-out",
        "accordion-up": "accordion-up 0.2s ease-out",
        "shake-smooth": "shake-smooth 0.5s ease-in-out"
      }
    }
  }
};
