'use client';

import * as React from 'react';
import {
  ColumnDef,
  ColumnFiltersState,
  SortingState,
  VisibilityState,
  flexRender,
  getCoreRowModel,
  getFilteredRowModel,
  getPaginationRowModel,
  getSortedRowModel,
  useReactTable,
  PaginationState,
  Column,
  Row,
} from '@tanstack/react-table';
import { motion, AnimatePresence } from 'motion/react';
import {
  ChevronDown,
  ChevronUp,
  Search,
  Eye,
  Filter,
  ArrowLeft,
  ArrowRight,
} from 'lucide-react';

import { cn } from '@/lib/utils';
import { Button } from '@/components/ui/button';
import {
  DropdownMenu,
  DropdownMenuCheckboxItem,
  DropdownMenuContent,
  DropdownMenuTrigger,
} from '@/components/ui/dropdown-menu';

const Input = React.forwardRef<HTMLInputElement, React.ComponentProps<'input'>>(
  ({ className, type, ...props }, ref) => {
    return (
      <input
        type={type}
        className={cn(
          'flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-base ring-offset-background file:border-0 file:bg-transparent file:text-sm file:font-medium file:text-foreground placeholder:text-muted-foreground focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 md:text-sm',
          className,
        )}
        ref={ref}
        {...props}
      />
    );
  },
);
Input.displayName = 'Input';

const Select = React.forwardRef<
  HTMLSelectElement,
  React.ComponentProps<'select'>
>(({ className, ...props }, ref) => {
  return (
    <select
      className={cn(
        'flex h-10 w-full rounded-md border border-input bg-background px-3 py-2 text-base ring-offset-background focus-visible:outline-hidden focus-visible:ring-2 focus-visible:ring-ring focus-visible:ring-offset-2 disabled:cursor-not-allowed disabled:opacity-50 md:text-sm',
        className,
      )}
      ref={ref}
      {...props}
    />
  );
});
Select.displayName = 'Select';

function Table({ className, ...props }: React.ComponentProps<'table'>) {
  return (
    <div
      data-slot='table-container'
      className='relative w-full overflow-x-auto'
    >
      <table
        data-slot='table'
        className={cn('w-full caption-bottom text-sm', className)}
        {...props}
      />
    </div>
  );
}

function TableHeader({ className, ...props }: React.ComponentProps<'thead'>) {
  return (
    <thead
      data-slot='table-header'
      className={cn('[&_tr]:border-b', className)}
      {...props}
    />
  );
}

function TableBody({ className, ...props }: React.ComponentProps<'tbody'>) {
  return (
    <tbody
      data-slot='table-body'
      className={cn('[&_tr:last-child]:border-0', className)}
      {...props}
    />
  );
}

function TableFooter({ className, ...props }: React.ComponentProps<'tfoot'>) {
  return (
    <tfoot
      data-slot='table-footer'
      className={cn(
        'bg-muted/50 border-t font-medium last:[&>tr]:border-b-0',
        className,
      )}
      {...props}
    />
  );
}

function TableRow({ className, ...props }: React.ComponentProps<'tr'>) {
  return (
    <tr
      data-slot='table-row'
      className={cn(
        'hover:bg-muted/50 data-[state=selected]:bg-muted border-b transition-colors',
        className,
      )}
      {...props}
    />
  );
}

function TableHead({ className, ...props }: React.ComponentProps<'th'>) {
  return (
    <th
      data-slot='table-head'
      className={cn(
        'text-foreground h-10 px-2 text-left align-middle font-medium whitespace-nowrap [&:has([role=checkbox])]:pr-0 *:[[role=checkbox]]:translate-y-0.5',
        className,
      )}
      {...props}
    />
  );
}

function TableCell({ className, ...props }: React.ComponentProps<'td'>) {
  return (
    <td
      data-slot='table-cell'
      className={cn(
        'p-2 align-middle whitespace-nowrap [&:has([role=checkbox])]:pr-0 *:[[role=checkbox]]:translate-y-0.5',
        className,
      )}
      {...props}
    />
  );
}

function TableCaption({
  className,
  ...props
}: React.ComponentProps<'caption'>) {
  return (
    <caption
      data-slot='table-caption'
      className={cn('text-muted-foreground mt-4 text-sm', className)}
      {...props}
    />
  );
}

interface DataTableProps<TData, TValue> {
  columns: ColumnDef<TData, TValue>[];
  data: TData[];
  searchable?: boolean;
  searchPlaceholder?: string;
  searchColumnKey?: string;
  globalSearch?: boolean;
  pagination?: boolean;
  pageSize?: number;
  pageSizeOptions?: number[];
  columnVisibility?: boolean;
  enableAnimations?: boolean;
  staggerDelay?: number;
  className?: string;
  tableClassName?: string;
  headerClassName?: string;
  rowClassName?: string;
  cellClassName?: string;
  showToolbar?: boolean;
  toolbarClassName?: string;
  customToolbar?: React.ReactNode;
  emptyMessage?: string;
  onRowClick?: (row: TData) => void;
  onRowSelect?: (selectedRows: TData[]) => void;
  initialSorting?: SortingState;
  initialColumnFilters?: ColumnFiltersState;
  initialColumnVisibility?: VisibilityState;
  initialGlobalFilter?: string;
  initialPagination?: PaginationState;
  enableScroll?: boolean;
  scrollHeight?: string | number;
  stickyHeader?: boolean;
  showFilters?: boolean;
  showPageSizeSelector?: boolean;
}

function DataTable<TData, TValue>({
  columns,
  data,
  searchable = true,
  searchPlaceholder = 'Search...',
  globalSearch = true,
  pagination = true,
  pageSize = 5,
  pageSizeOptions = [5, 10, 20, 30, 40, 50],
  columnVisibility = true,
  enableAnimations = true,
  staggerDelay = 0.05,
  className,
  tableClassName,
  rowClassName,
  cellClassName,
  showToolbar = true,
  toolbarClassName,
  customToolbar,
  emptyMessage = 'No results found.',
  onRowClick,
  onRowSelect,
  initialSorting = [],
  initialColumnFilters = [],
  initialColumnVisibility = {},
  initialGlobalFilter = '',
  initialPagination = { pageIndex: 0, pageSize },
  enableScroll = false,
  scrollHeight = '400px',
  showFilters = false,
  showPageSizeSelector = true,
}: DataTableProps<TData, TValue>) {
  const [sorting, setSorting] = React.useState<SortingState>(initialSorting);
  const [columnFilters, setColumnFilters] =
    React.useState<ColumnFiltersState>(initialColumnFilters);
  const [columnVisibilityState, setColumnVisibilityState] =
    React.useState<VisibilityState>(initialColumnVisibility);
  const [globalFilter, setGlobalFilter] = React.useState(initialGlobalFilter);
  const [paginationState, setPaginationState] =
    React.useState<PaginationState>(initialPagination);
  const [rowSelection, setRowSelection] = React.useState({});
  const [showFiltersPanel, setShowFiltersPanel] = React.useState(false);

  // eslint-disable-next-line react-hooks/incompatible-library
  const table = useReactTable({
    data,
    columns,
    onSortingChange: setSorting,
    onColumnFiltersChange: setColumnFilters,
    getCoreRowModel: getCoreRowModel(),
    getPaginationRowModel:
      pagination && !enableScroll ? getPaginationRowModel() : undefined,
    getSortedRowModel: getSortedRowModel(),
    getFilteredRowModel: getFilteredRowModel(),
    onColumnVisibilityChange: setColumnVisibilityState,
    onGlobalFilterChange: globalSearch ? setGlobalFilter : undefined,
    onPaginationChange:
      pagination && !enableScroll ? setPaginationState : undefined,
    onRowSelectionChange: setRowSelection,
    globalFilterFn: 'includesString',
    state: {
      sorting,
      columnFilters,
      columnVisibility: columnVisibilityState,
      globalFilter: globalSearch ? globalFilter : undefined,
      pagination: pagination && !enableScroll ? paginationState : undefined,
      rowSelection,
    },
    initialState: {
      pagination: pagination && !enableScroll ? initialPagination : undefined,
    },
  });

  React.useEffect(() => {
    if (onRowSelect) {
      const selectedRows = table
        .getFilteredSelectedRowModel()
        .rows.map((row) => row.original);
      onRowSelect(selectedRows);
    }
  }, [rowSelection, onRowSelect, table]);

  const TableWrapper = enableAnimations ? motion.div : 'div';
  const ToolbarWrapper = enableAnimations ? motion.div : 'div';
  const TableContainerWrapper = enableAnimations ? motion.div : 'div';
  const PaginationWrapper = enableAnimations ? motion.div : 'div';
  const RowWrapper = enableAnimations ? motion.tr : 'tr';
  const FilterPanelWrapper = enableAnimations ? motion.div : 'div';

  const animationProps = enableAnimations
    ? {
        initial: { opacity: 0, y: 20 },
        animate: { opacity: 1, y: 0 },
        transition: { duration: 0.5 },
      }
    : {};

  const toolbarAnimationProps = enableAnimations
    ? {
        initial: { opacity: 0, y: -10 },
        animate: { opacity: 1, y: 0 },
        transition: { duration: 0.4, delay: 0.1 },
      }
    : {};

  const tableAnimationProps = enableAnimations
    ? {
        initial: { opacity: 0, scale: 0.95 },
        animate: { opacity: 1, scale: 1 },
        transition: { duration: 0.4, delay: 0.2 },
      }
    : {};

  const paginationAnimationProps = enableAnimations
    ? {
        initial: { opacity: 0, y: 10 },
        animate: { opacity: 1, y: 0 },
        transition: { duration: 0.4, delay: 0.3 },
      }
    : {};

  const filterPanelAnimationProps = enableAnimations
    ? {
        initial: { opacity: 0, height: 0 },
        animate: { opacity: 1, height: 'auto' },
        exit: { opacity: 0, height: 0 },
        transition: { duration: 0.3 },
      }
    : {};

  return (
    <TableWrapper
      className={cn('w-full space-y-4', className)}
      {...animationProps}
    >
      {showToolbar && (
        <ToolbarWrapper
          className={cn('flex flex-col gap-4', toolbarClassName)}
          {...toolbarAnimationProps}
        >
          {customToolbar ? (
            customToolbar
          ) : (
            <>
              <div className='flex flex-col sm:flex-row items-start sm:items-center justify-between gap-4'>
                <div className='flex flex-col sm:flex-row items-start sm:items-center gap-2 w-full sm:w-auto'>
                  {searchable && globalSearch && (
                    <div className='relative w-full sm:w-80'>
                      <Search className='absolute left-3 top-1/2 transform -translate-y-1/2 h-4 w-4 text-muted-foreground' />
                      <Input
                        placeholder={searchPlaceholder}
                        value={globalFilter ?? ''}
                        onChange={(event) =>
                          setGlobalFilter(event.target.value)
                        }
                        className='pl-10'
                      />
                    </div>
                  )}
                </div>

                <div className='flex items-center gap-2'>
                  {showFilters && (
                    <Button
                      variant='outline'
                      onClick={() => setShowFiltersPanel(!showFiltersPanel)}
                      className='gap-2'
                    >
                      <Filter className='h-4 w-4' />
                      Filters
                      <ChevronDown
                        className={cn(
                          'h-4 w-4 transition-transform',
                          showFiltersPanel && 'rotate-180',
                        )}
                      />
                    </Button>
                  )}

                  {columnVisibility && (
                    <DropdownMenu>
                      <DropdownMenuTrigger asChild>
                        <Button variant='outline' className='gap-2'>
                          <Eye className='h-4 w-4' />
                          View
                          <ChevronDown className='h-4 w-4' />
                        </Button>
                      </DropdownMenuTrigger>
                      <DropdownMenuContent className='w-48'>
                        {table
                          .getAllColumns()
                          .filter((column) => column.getCanHide())
                          .map((column) => {
                            return (
                              <DropdownMenuCheckboxItem
                                key={column.id}
                                className='capitalize'
                                checked={column.getIsVisible()}
                                onCheckedChange={(value: boolean) =>
                                  column.toggleVisibility(!!value)
                                }
                              >
                                {column.id}
                              </DropdownMenuCheckboxItem>
                            );
                          })}
                      </DropdownMenuContent>
                    </DropdownMenu>
                  )}
                </div>
              </div>

              {showFilters && enableAnimations && (
                <AnimatePresence>
                  {showFiltersPanel && (
                    <FilterPanelWrapper
                      className='border rounded-lg p-4 bg-muted/10'
                      {...filterPanelAnimationProps}
                    >
                      <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4'>
                        {table
                          .getAllColumns()
                          .filter((column) => column.getCanFilter())
                          .map((column) => (
                            <div key={column.id} className='space-y-2'>
                              <label className='text-sm font-medium capitalize'>
                                {column.id}
                              </label>
                              <Input
                                placeholder={`Filter ${column.id}...`}
                                value={
                                  (column.getFilterValue() as string) ?? ''
                                }
                                onChange={(event) =>
                                  column.setFilterValue(event.target.value)
                                }
                              />
                            </div>
                          ))}
                      </div>
                    </FilterPanelWrapper>
                  )}
                </AnimatePresence>
              )}

              {showFilters && !enableAnimations && showFiltersPanel && (
                <div className='border rounded-lg p-4 bg-muted/10'>
                  <div className='grid grid-cols-1 sm:grid-cols-2 lg:grid-cols-3 gap-4'>
                    {table
                      .getAllColumns()
                      .filter((column) => column.getCanFilter())
                      .map((column) => (
                        <div key={column.id} className='space-y-2'>
                          <label className='text-sm font-medium capitalize'>
                            {column.id}
                          </label>
                          <Input
                            placeholder={`Filter ${column.id}...`}
                            value={(column.getFilterValue() as string) ?? ''}
                            onChange={(event) =>
                              column.setFilterValue(event.target.value)
                            }
                          />
                        </div>
                      ))}
                  </div>
                </div>
              )}
            </>
          )}
        </ToolbarWrapper>
      )}

      <TableContainerWrapper
        className='rounded-lg border bg-card shadow-xs overflow-hidden'
        {...tableAnimationProps}
      >
        <div className='overflow-x-auto'>
          <div className='min-w-max'>
            <Table className={tableClassName}>
              <TableHeader>
                {table.getHeaderGroups().map((headerGroup) => (
                  <TableRow key={headerGroup.id}>
                    {headerGroup.headers.map((header) => (
                      <TableHead
                        key={header.id}
                        className='sticky top-0 z-20 bg-card border-b shadow-xs whitespace-nowrap'
                      >
                        {header.isPlaceholder
                          ? null
                          : flexRender(
                              header.column.columnDef.header,
                              header.getContext(),
                            )}
                      </TableHead>
                    ))}
                  </TableRow>
                ))}
              </TableHeader>
            </Table>
            <div
              className='overflow-y-auto'
              style={{
                maxHeight: enableScroll ? scrollHeight : 'auto',
              }}
            >
              <Table className={tableClassName}>
                <TableBody>
                  {enableAnimations ? (
                    <AnimatePresence mode='popLayout'>
                      {table.getRowModel().rows?.length ? (
                        table.getRowModel().rows.map((row, index) => (
                          <RowWrapper
                            key={row.id}
                            layout
                            initial={{ opacity: 0, y: 20 }}
                            animate={{ opacity: 1, y: 0 }}
                            exit={{ opacity: 0, y: -20 }}
                            transition={{
                              duration: 0.3,
                              delay: index * staggerDelay,
                            }}
                            data-state={row.getIsSelected() && 'selected'}
                            className={cn(
                              'border-b transition-colors hover:bg-muted/50 group cursor-pointer',
                              rowClassName,
                            )}
                            onClick={() => onRowClick?.(row.original)}
                          >
                            {row.getVisibleCells().map((cell) => (
                              <TableCell
                                key={cell.id}
                                className={cn(
                                  'group-hover:bg-transparent whitespace-nowrap',
                                  cellClassName,
                                )}
                              >
                                {flexRender(
                                  cell.column.columnDef.cell,
                                  cell.getContext(),
                                )}
                              </TableCell>
                            ))}
                          </RowWrapper>
                        ))
                      ) : (
                        <motion.tr
                          initial={{ opacity: 0 }}
                          animate={{ opacity: 1 }}
                          transition={{ duration: 0.3 }}
                        >
                          <TableCell
                            colSpan={columns.length}
                            className='h-24 text-center'
                          >
                            {emptyMessage}
                          </TableCell>
                        </motion.tr>
                      )}
                    </AnimatePresence>
                  ) : (
                    <>
                      {table.getRowModel().rows?.length ? (
                        table.getRowModel().rows.map((row) => (
                          <TableRow
                            key={row.id}
                            data-state={row.getIsSelected() && 'selected'}
                            className={cn('cursor-pointer', rowClassName)}
                            onClick={() => onRowClick?.(row.original)}
                          >
                            {row.getVisibleCells().map((cell) => (
                              <TableCell
                                key={cell.id}
                                className='whitespace-nowrap'
                              >
                                {flexRender(
                                  cell.column.columnDef.cell,
                                  cell.getContext(),
                                )}
                              </TableCell>
                            ))}
                          </TableRow>
                        ))
                      ) : (
                        <TableRow>
                          <TableCell
                            colSpan={columns.length}
                            className='h-24 text-center'
                          >
                            {emptyMessage}
                          </TableCell>
                        </TableRow>
                      )}
                    </>
                  )}
                </TableBody>
              </Table>
            </div>
          </div>
        </div>
      </TableContainerWrapper>

      {pagination && !enableScroll && (
        <PaginationWrapper
          className='flex flex-col sm:flex-row items-center justify-between gap-4'
          {...paginationAnimationProps}
        >
          <div className='flex items-center gap-4'>
            <div className='text-sm text-muted-foreground'>
              {table.getFilteredSelectedRowModel().rows.length} of{' '}
              {table.getFilteredRowModel().rows.length} row(s) selected.
            </div>

            {showPageSizeSelector && (
              <div className='flex items-center gap-2'>
                <span className='text-sm text-muted-foreground'>
                  Rows per page:
                </span>
                <DropdownMenu>
                  <DropdownMenuTrigger asChild>
                    <Button variant='outline' className='w-20 justify-between'>
                      {table.getState().pagination.pageSize}
                      <ChevronDown className='ml-2 h-4 w-4' />
                    </Button>
                  </DropdownMenuTrigger>

                  <DropdownMenuContent className='w-28'>
                    {pageSizeOptions.map((size) => (
                      <DropdownMenuCheckboxItem
                        key={size}
                        checked={table.getState().pagination.pageSize === size}
                        onCheckedChange={() => table.setPageSize(size)}
                      >
                        {size}
                      </DropdownMenuCheckboxItem>
                    ))}
                  </DropdownMenuContent>
                </DropdownMenu>
              </div>
            )}
          </div>

          <div className='flex items-center space-x-2'>
            <p className='text-sm text-muted-foreground'>
              Page {table.getState().pagination.pageIndex + 1} of{' '}
              {table.getPageCount()}
            </p>
            <div className='flex items-center space-x-2'>
              <Button
                variant='outline'
                size='sm'
                onClick={() => table.previousPage()}
                disabled={!table.getCanPreviousPage()}
                className='h-8 w-8 p-0'
              >
                <ArrowLeft className='h-4 w-4' />
              </Button>
              <Button
                variant='outline'
                size='sm'
                onClick={() => table.nextPage()}
                disabled={!table.getCanNextPage()}
                className='h-8 w-8 p-0'
              >
                <ArrowRight className='h-4 w-4' />
              </Button>
            </div>
          </div>
        </PaginationWrapper>
      )}
    </TableWrapper>
  );
}

const createSortableHeader = <TData, TValue = unknown>(
  title: string,
  enableAnimations: boolean = true,
) => {
  const SortableHeader: React.FC<{ column: Column<TData, TValue> }> = ({
    column,
  }) => {
    const SortButton = enableAnimations ? motion.div : 'div';

    return (
      <Button
        variant='ghost'
        onClick={() => column.toggleSorting(column.getIsSorted() === 'asc')}
        className='hover:bg-transparent p-0 h-auto'
      >
        {title}
        {enableAnimations ? (
          <SortButton
            animate={{ rotate: column.getIsSorted() === 'desc' ? 180 : 0 }}
            transition={{ duration: 0.2 }}
          >
            {column.getIsSorted() ? (
              <ChevronUp className='ml-2 h-4 w-4' />
            ) : (
              <ChevronDown className='ml-2 h-4 w-4' />
            )}
          </SortButton>
        ) : (
          <>
            {column.getIsSorted() ? (
              <ChevronUp className='ml-2 h-4 w-4' />
            ) : (
              <ChevronDown className='ml-2 h-4 w-4' />
            )}
          </>
        )}
      </Button>
    );
  };

  SortableHeader.displayName = 'SortableHeader';
  return SortableHeader;
};

interface AnimatedCellProps {
  row: Row<unknown>;
  getValue: () => unknown;
}

const createAnimatedCell = (
  enableAnimations: boolean = true,
  delay: number = 0,
  className?: string,
) => {
  const AnimatedCell: React.FC<AnimatedCellProps> = ({ row, getValue }) => {
    const CellWrapper = enableAnimations ? motion.div : 'div';
    const animationProps = enableAnimations
      ? {
          initial: { opacity: 0, x: -20 },
          animate: { opacity: 1, x: 0 },
          transition: { duration: 0.3, delay: row.index * 0.05 + delay },
        }
      : {};

    return (
      <CellWrapper className={className} {...animationProps}>
        {getValue() as React.ReactNode}
      </CellWrapper>
    );
  };

  AnimatedCell.displayName = 'AnimatedCell';
  return AnimatedCell;
};

export {
  Table,
  TableHeader,
  TableBody,
  TableFooter,
  TableHead,
  TableRow,
  TableCell,
  TableCaption,
  DataTable,
  createSortableHeader,
  createAnimatedCell,
};

export type { ColumnDef } from '@tanstack/react-table';

import * as React from 'react';
import { Slot } from '@radix-ui/react-slot';
import { cva, type VariantProps } from 'class-variance-authority';

import { cn } from '@/lib/utils';

const buttonVariants = cva(
  'inline-flex items-center justify-center gap-2 whitespace-nowrap rounded-md text-sm font-medium transition-colors focus-visible:outline-hidden focus-visible:ring-1 focus-visible:ring-ring disabled:pointer-events-none disabled:opacity-50 [&_svg]:pointer-events-none [&_svg]:size-4 [&_svg]:shrink-0',
  {
    variants: {
      variant: {
        default:
          'bg-primary text-primary-foreground shadow-sm hover:bg-primary/90',
        destructive:
          'bg-destructive text-destructive-foreground shadow-xs hover:bg-destructive/90',
        outline:
          'border border-input bg-background shadow-xs hover:bg-accent hover:text-accent-foreground',
        secondary:
          'bg-secondary text-secondary-foreground shadow-xs hover:bg-secondary/80',
        ghost: 'hover:bg-accent hover:text-accent-foreground',
        link: 'text-primary underline-offset-4 hover:underline',
        success: 'bg-green-600 text-white hover:bg-green-700',
        warning: 'bg-yellow-500 text-black hover:bg-yellow-600',
        info: 'bg-blue-500 text-white hover:bg-blue-600',
        dark: 'bg-gray-800 text-white hover:bg-gray-700',
        light: 'bg-gray-100 text-gray-800 hover:bg-gray-200',
        gradient:
          'bg-linear-to-r from-indigo-500 via-purple-500 to-pink-500 text-white hover:opacity-90',
        glass:
          'bg-white/10 backdrop-blur-md text-white border border-white/20 hover:bg-white/20',
      },
      size: {
        default: 'h-9 px-4 py-2',
        sm: 'h-8 rounded-md px-3 text-xs',
        lg: 'h-10 rounded-md px-8',
        icon: 'h-9 w-9',
      },
    },
    defaultVariants: {
      variant: 'default',
      size: 'default',
    },
  },
);

export interface ButtonProps
  extends
    React.ButtonHTMLAttributes<HTMLButtonElement>,
    VariantProps<typeof buttonVariants> {
  asChild?: boolean;
}

const Button = React.forwardRef<HTMLButtonElement, ButtonProps>(
  ({ className, variant, size, asChild = false, ...props }, ref) => {
    const Comp = asChild ? Slot : 'button';
    return (
      <Comp
        className={cn(buttonVariants({ variant, size, className }))}
        ref={ref}
        {...props}
      />
    );
  },
);
Button.displayName = 'Button';

export { Button, buttonVariants };

'use client';

import * as React from 'react';
import { useEffect, useState } from 'react';
import * as DropdownMenuPrimitive from '@radix-ui/react-dropdown-menu';
import { CheckIcon, ChevronRightIcon, CircleIcon } from 'lucide-react';
import { motion, AnimatePresence, Variants } from 'motion/react';
import { cn } from '@/lib/utils';

const dropdownVariants: Variants = {
  hidden: {
    opacity: 0,
    scale: 0.5,
    rotateX: 40,
    y: 20,
  },
  visible: {
    opacity: 1,
    scale: 1,
    y: 0,
    rotateX: 0,
    transition: {
      type: 'spring' as const,
      stiffness: 260,
      damping: 15,
    },
  },
  exit: {
    opacity: 0,
    scale: 0.8,
    rotateX: 10,
    y: 10,
    transition: {
      duration: 0.2,
    },
  },
};

const itemVariants: Variants = {
  hidden: {
    opacity: 0,
    x: -20,
  },
  visible: (i: number) => ({
    opacity: 1,
    x: 0,
    transition: {
      delay: i * 0.05,
      duration: 0.2,
    },
  }),
  exit: {
    opacity: 0,
    x: -20,
    transition: {
      duration: 0.1,
    },
  },
};

const useMobile = () => {
  const [isMobile, setIsMobile] = useState(false);

  useEffect(() => {
    const checkIfMobile = () => {
      setIsMobile(
        window.innerWidth < 768 ||
          /Android|webOS|iPhone|iPad|iPod|BlackBerry|IEMobile|Opera Mini/i.test(
            navigator.userAgent,
          ),
      );
    };

    checkIfMobile();
    window.addEventListener('resize', checkIfMobile);

    return () => {
      window.removeEventListener('resize', checkIfMobile);
    };
  }, []);

  return isMobile;
};

const DropdownMenuContext = React.createContext<{ animate: boolean }>({
  animate: true,
});

function DropdownMenu({
  animate = true,
  ...props
}: React.ComponentProps<typeof DropdownMenuPrimitive.Root> & {
  animate?: boolean;
}) {
  return (
    <DropdownMenuContext.Provider value={{ animate }}>
      <DropdownMenuPrimitive.Root data-slot='dropdown-menu' {...props} />
    </DropdownMenuContext.Provider>
  );
}

function DropdownMenuTrigger({
  className,
  children,
  ...props
}: React.ComponentProps<typeof DropdownMenuPrimitive.Trigger>) {
  const { animate } = React.useContext(DropdownMenuContext);

  return (
    <DropdownMenuPrimitive.Trigger
      data-slot='dropdown-menu-trigger'
      className={cn(
        'inline-flex items-center justify-center rounded-lg text-sm font-medium transition-all duration-200',
        'hover:shadow-lg',
        className,
      )}
      asChild
      {...props}
    >
      <motion.div
        whileHover={animate ? { scale: 1.02 } : undefined}
        whileTap={animate ? { scale: 0.98 } : undefined}
        transition={
          animate ? { type: 'spring', stiffness: 400, damping: 25 } : undefined
        }
        className='flex items-center gap-2 cursor-pointer'
        style={{ touchAction: 'manipulation' }}
      >
        {children}
      </motion.div>
    </DropdownMenuPrimitive.Trigger>
  );
}

interface DropdownMenuContentProps extends React.ComponentPropsWithoutRef<
  typeof DropdownMenuPrimitive.Content
> {
  className?: string;
  sideOffset?: number;
  children?: React.ReactNode;
  maxHeight?: string | number;
}

function DropdownMenuContent({
  className,
  sideOffset = 4,
  maxHeight = '16rem',
  children,
  ...props
}: DropdownMenuContentProps) {
  const { animate } = React.useContext(DropdownMenuContext);

  return (
    <DropdownMenuPrimitive.Portal>
      <DropdownMenuPrimitive.Content
        data-slot='dropdown-menu-content'
        sideOffset={sideOffset}
        className='z-50'
        asChild
        {...props}
      >
        <motion.div
          variants={animate ? dropdownVariants : undefined}
          initial={animate ? 'hidden' : undefined}
          animate={animate ? 'visible' : undefined}
          exit={animate ? 'exit' : undefined}
          className={cn(
            'w-72 rounded-xl border shadow-xl overflow-hidden perspective-midrange transform-3d',
            'bg-[#ffffff] border-neutral-900/10',
            'dark:bg-[#262626] dark:border-neutral-50/10',
            className,
          )}
          style={{
            transformOrigin:
              'var(--radix-dropdown-menu-content-transform-origin)',
          }}
        >
          <div
            className='relative z-20 overflow-y-auto scrollbar-visible'
            style={{
              maxHeight:
                typeof maxHeight === 'number' ? `${maxHeight}px` : maxHeight,
              scrollbarWidth: 'thin',
              scrollbarColor: 'rgba(155, 155, 155, 0.5) transparent',
            }}
          >
            <style jsx global>{`
              .scrollbar-visible::-webkit-scrollbar {
                width: 6px;
                display: block;
              }
              .scrollbar-visible::-webkit-scrollbar-track {
                background: transparent;
              }
              .scrollbar-visible::-webkit-scrollbar-thumb {
                background-color: rgba(155, 155, 155, 0.5);
                border-radius: 20px;
              }
              .scrollbar-visible::-webkit-scrollbar-thumb:hover {
                background-color: rgba(155, 155, 155, 0.7);
              }
            `}</style>
            <div className='p-2'>
              {React.Children.map(children, (child, index) =>
                animate ? (
                  <motion.div
                    key={index}
                    custom={index}
                    variants={itemVariants}
                    initial='hidden'
                    animate='visible'
                    exit='exit'
                  >
                    {child}
                  </motion.div>
                ) : (
                  <div key={index}>{child}</div>
                ),
              )}
            </div>
          </div>
        </motion.div>
      </DropdownMenuPrimitive.Content>
    </DropdownMenuPrimitive.Portal>
  );
}

function DropdownMenuItem({
  className,
  inset,
  variant = 'default',
  children,
  ...props
}: React.ComponentProps<typeof DropdownMenuPrimitive.Item> & {
  inset?: boolean;
  variant?: 'default' | 'destructive';
  className?: string;
}) {
  const [isHovered, setIsHovered] = useState(false);
  const isMobile = useMobile();
  const { animate } = React.useContext(DropdownMenuContext);

  return (
    <DropdownMenuPrimitive.Item
      data-slot='dropdown-menu-item'
      data-inset={inset}
      data-variant={variant}
      className={cn(
        'relative flex cursor-pointer items-center gap-3 rounded-lg p-2 text-sm outline-hidden select-none overflow-hidden',
        'transition-all duration-200 ease-out',
        'focus:outline-hidden focus:bg-transparent',
        'data-disabled:pointer-events-none data-disabled:opacity-50',
        'data-inset:pl-8',
        'text-neutral-900 dark:text-neutral-50',
        "[&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg:not([class*='size-'])]:size-4",
        className,
      )}
      onMouseEnter={() => !isMobile && setIsHovered(true)}
      onMouseLeave={() => !isMobile && setIsHovered(false)}
      style={{ touchAction: 'manipulation' }}
      asChild
      {...props}
    >
      <motion.div
        className='relative w-full'
        initial={animate ? { opacity: 0, x: -20 } : undefined}
        animate={animate ? { opacity: 1, x: 0 } : undefined}
      >
        <AnimatePresence>
          {!isMobile && isHovered && (
            <motion.div
              layoutId={animate ? 'hoverBackground' : undefined}
              initial={animate ? { opacity: 0 } : undefined}
              animate={
                animate
                  ? {
                      opacity: 1,
                      scale: 1.05,
                      transition: {
                        type: 'spring',
                        stiffness: 260,
                        damping: 15,
                      },
                    }
                  : { opacity: 1 }
              }
              exit={animate ? { opacity: 0 } : undefined}
              className={cn(
                'absolute inset-0 rounded-lg',
                variant === 'destructive'
                  ? 'bg-red-500/10 dark:bg-red-500/20'
                  : 'bg-[#f5f5f5] dark:bg-[#404040]',
              )}
            />
          )}
        </AnimatePresence>

        <div
          className={cn(
            'relative z-10 w-full flex items-center gap-3',
            variant === 'destructive' && 'text-red-600 dark:text-red-400',
          )}
        >
          {React.Children.map(children, (child, index) => {
            if (
              React.isValidElement(child) &&
              (child.type === 'svg' ||
                (child.props &&
                  typeof child.props === 'object' &&
                  'className' in child.props &&
                  typeof child.props.className === 'string' &&
                  child.props.className.includes('lucide')))
            ) {
              return (
                <motion.div
                  key={index}
                  animate={
                    animate
                      ? {
                          scale: !isMobile && isHovered ? 1.1 : 1,
                          rotate: !isMobile && isHovered ? 5 : 0,
                        }
                      : undefined
                  }
                  transition={
                    animate ? { type: 'spring', stiffness: 500 } : undefined
                  }
                >
                  {child}
                </motion.div>
              );
            }

            if (typeof child === 'string') {
              return (
                <motion.span
                  key={index}
                  animate={
                    animate
                      ? {
                          y: !isMobile && isHovered ? -1 : 0,
                          x: !isMobile && isHovered ? 1 : 0,
                        }
                      : undefined
                  }
                  transition={
                    animate ? { type: 'spring', stiffness: 500 } : undefined
                  }
                  className='font-medium flex-1'
                >
                  {child}
                </motion.span>
              );
            }

            return child;
          })}
        </div>
      </motion.div>
    </DropdownMenuPrimitive.Item>
  );
}

function DropdownMenuCheckboxItem({
  className,
  children,
  checked,
  ...props
}: React.ComponentProps<typeof DropdownMenuPrimitive.CheckboxItem> & {
  className?: string;
}) {
  const [isHovered, setIsHovered] = useState(false);
  const isMobile = useMobile();
  const { animate } = React.useContext(DropdownMenuContext);

  const handleSelect = (e: Event) => {
    e.preventDefault();
    setTimeout(() => {
      const event = new KeyboardEvent('keydown', { key: 'Escape' });
      document.dispatchEvent(event);
    }, 150);
    if (props.onSelect) props.onSelect(e);
  };

  return (
    <DropdownMenuPrimitive.CheckboxItem
      data-slot='dropdown-menu-checkbox-item'
      className={cn(
        'relative flex cursor-pointer items-center gap-3 rounded-lg py-2 pr-3 pl-8 text-sm outline-hidden select-none overflow-hidden',
        'transition-all duration-200 ease-out',
        'focus:outline-hidden focus:bg-transparent',
        'data-disabled:pointer-events-none data-disabled:opacity-50',
        'text-neutral-900 dark:text-neutral-50',
        '[&_svg]:pointer-events-none [&_svg]:shrink-0 [&_svg]:size-4',
        className,
      )}
      checked={checked}
      onMouseEnter={() => !isMobile && setIsHovered(true)}
      onMouseLeave={() => !isMobile && setIsHovered(false)}
      onSelect={handleSelect}
      style={{ touchAction: 'manipulation' }}
      asChild
      {...props}
    >
      <motion.div className='relative w-full'>
        <AnimatePresence>
          {!isMobile && isHovered && (
            <motion.div
              layoutId={animate ? 'checkboxHoverBackground' : undefined}
              initial={animate ? { opacity: 0 } : undefined}
              animate={
                animate
                  ? {
                      opacity: 1,
                      scale: 1.05,
                      transition: {
                        type: 'spring',
                        stiffness: 260,
                        damping: 15,
                      },
                    }
                  : { opacity: 1 }
              }
              exit={animate ? { opacity: 0 } : undefined}
              className='absolute inset-0 rounded-lg bg-[#f5f5f5] dark:bg-[#404040]'
            />
          )}
        </AnimatePresence>

        <span className='pointer-events-none absolute left-2 flex size-4 items-center justify-center'>
          <DropdownMenuPrimitive.ItemIndicator>
            <motion.div
              initial={animate ? { scale: 0 } : undefined}
              animate={animate ? { scale: 1 } : undefined}
              transition={
                animate
                  ? { type: 'spring', stiffness: 400, damping: 25 }
                  : undefined
              }
            >
              <CheckIcon className='size-4' />
            </motion.div>
          </DropdownMenuPrimitive.ItemIndicator>
        </span>

        <motion.div
          animate={
            animate
              ? {
                  y: !isMobile && isHovered ? -1 : 0,
                  x: !isMobile && isHovered ? 1 : 0,
                }
              : undefined
          }
          transition={animate ? { type: 'spring', stiffness: 500 } : undefined}
          className='relative z-10'
        >
          {children}
        </motion.div>
      </motion.div>
    </DropdownMenuPrimitive.CheckboxItem>
  );
}

